#include<stdio.h>
void bubble_sort(int arr[],int sz)
{
	int i,tmp,j;
	for(i=0;i<sz-1;i++){
		int flag=1;//假设要排序的数据已经有序
		for(j=0;j<sz-i-1;j++){
			if(arr[j]>arr[j+1]){
				tmp=arr[j];
				arr[j]=arr[j+1];
				arr[j+1]=tmp;
				flag=0;//本趟排序的数据其实不完全有序
			}
		}
		if(flag==1)
			break;
	}
}
int main()
{
	int i,n;
	int arr[1000];
	while(scanf("%d",&n)!=EOF){
		for(i=0;i<n;i++)
			scanf("%d",&arr[i]);
		bubble_sort(arr,n);
		for(i=0;i<n;i++){
			printf("%d",arr[i]);
			if(i!=n-1)
				printf(" ");
		}
		printf("\n");
	}
	return 0;
}
